import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Redirect, useParams } from "react-router-dom";
import { editNewSong, playAllSongs } from "../store/songFile"
// import Navigation from "../Navigation";

function EditSong() {
    const sessionUser = useSelector((state) => state.session.user);
    const dispatch = useDispatch();
    const songId = useParams().id;
    const song = useSelector((state) => state.songFile[songId]);
    const [songTitle, setSongTitle] = useState(`${song.title}`);
    const [errors, setErrors] = useState([]);

    const changeSong = async (e) => {
        e.preventDefault();
        const editSong = {
            id: songId,
            title: songTitle
        };

        const newEdit = dispatch(editNewSong(editSong).catch(
            async (res) => {
                const data = await res.json();
                if (data && data.errors) {
                    setErrors(data.errors);
                } else if (data && data.message) {
                    setErrors([data.message]);
                }
            }
        ))

        if (newEdit) {
             dispatch(playAllSongs());
        }
    }

    if (!sessionUser) return <Redirect to='/' />;

    return (
        <div>
            <p>{song.title}</p>
            <form onSubmit={changeSong}>
                <ul>
                    {errors.map((error) => (
                        <li key={error}>{error}</li>
                    ))}
                </ul>
                <input
                    name="title"
                    type="text"
                    value={songTitle}
                    onChange={(e) => {
                        setSongTitle(e.target.value);
                    }}
                ></input>
                <button type="submit">Submit</button>
            </form>
        </div>
    )
}


export default EditSong;

