import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Redirect, useParams } from "react-router-dom";
import { editNewSong, playAllSongs } from "../store/songFile"
// import Navigation from "../Navigation";

function EditSong() {
    const sessionUser = useSelector((state) => state.session.user);
    const dispatch = useDispatch();
    const songId = useParams().id;
    const song = useSelector((state) => state.songFile[songId]);
    const [songTitle, setSongTitle] = useState(`${song.title}`);
    const [errors, setErrors] = useState([]);

    const changeSongTitle = async (e) => {
        e.preventDefault();
        const editSong = {
            songId,
            songTitle
        };

        const newEdit = await dispatch(editNewSong(editSong).catch(
            async (res) => {
                const data = await res.json();
                if (data && data.errors) {
                    setErrors(data.errors);
                } else if (data && data.message) {
                    setErrors([data.message]);
                }
            }
        ))

        if (newEdit) {
            await dispatch(playAllSongs());
        }
    }


    const btn = document.getElementById('btn');

    btn.addEventListener('click', () => {
        const form = document.getElementById('form');

        if (form.style.visibility === 'hidden') {
            form.style.visibility = 'visible';
        } else {
            form.style.visibility = 'hidden';
        }
    });

    if (!sessionUser) return <Redirect to='/' />;

    return (
        // isLoaded && (
        //     <div>

        //     </div>

        // )
        // < div >
        <div>
            <p>{song.title}</p>
            <form id="btn" onSubmit={changeSongTitle}>
                <input
                    name="title"
                    type="text"
                    value={songTitle}
                    onChange={(e) => {
                        setSongTitle(e.target.value);
                    }}
                ></input>
                <button type="submit">Submit</button>
            </form>
        </div >
    )
}


export default EditSong;
