import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Redirect, useParams } from "react-router-dom";
import { editCurrentSong, playAllSongs } from "../store/songFile"


function EditSong() {
    const sessionUser = useSelector((state) => state.session.user);
    const dispatch = useDispatch();
    const songId = useParams().id;

    const song = useSelector((state) => state.songFile[songId]);
    // console.log(song)
    const [songTitle, setSongTitle] = useState(`${song.title}`);
    // const [isLoaded, setIsLoaded] = useState(false);
    const [errors, setErrors] = useState([]);

    if (!sessionUser) return <Redirect to='/' />;

    const changeSong = async (e) => {
        e.preventDefault();
        const editSong = {
            id: songId,
            title: songTitle
        };

        const newEdit = dispatch(editCurrentSong(editSong).catch(
            async (res) => {
                const data = await res.json();
                if (data && data.errors) {
                    setErrors(data.errors);
                } else if (data && data.message) {
                    setErrors([data.message]);
                }
            }
        ))

        if (newEdit) {
            dispatch(playAllSongs());
            return;
        }
    }


    return (
            <div>
                <form onSubmit={changeSong}>
                <p>{song.title}</p>
                    <input
                        name="title"
                        type="text"
                        value={songTitle}
                        onChange={(e) => {
                            setSongTitle(e.target.value);
                        }}
                    />
                    <button type="submit">Submit</button>
                </form>
            </div>
        )
}


export default EditSong;
