import { csrfFetch } from "./csrf";

const GET_COMMENTS = "/api/GETCOMMENTS";
const ADD_COMMENT = "/api/ADDCOMMENT";
const DELETE_COMMENT = "/api/DELETECOMMENT";

const getComments = (comments) => ({
    type: GET_COMMENTS,
    comments
})

const addComment = (comment) => ({
    type: ADD_COMMENT,
    comment
})

const deleteComment = (comment) => ({
    type: DELETE_COMMENT,
    comment
})

export const getAllComments = () => async (dispatch) => {
    const response = await csrfFetch(`/api/songs/${id}/comments`, {
        method: "GET"
    });

    if (response.ok) {
        const data = await response.json();
        dispatch(getComments(data));
    }
};

export const createComment = (comments) => async (dispatch) => {
    const { userId, songId, body} = comments;
    const response = await csrfFetch(`/api/songs/${songId}/comments`, {
        method: "POST",
        body: JSON.stringify({
            userId,
            songId,
            body
        })
    });
    if (response.ok) {
        const data = await response.json();
        dispatch(addComment(data));
        return data;
    }
}

// export const removeComment = (payload) => async (dispatch) => {
//     const commentId = payload.comment.id;
//     console.log(commentId)
// }

const initialState = {};

const commentReducer = (state = initialState, action) => {
    let newState = { ...state };
    switch (action.type) {
        case GET_COMMENTS:
            newState = {};
            action.comments.forEach(comment => (newState[comment.id] = comment));
            return newState;

        case ADD_COMMENT:
            return { ...state, [action.comment.id]: action.comment };


        default:
            return state;
    }
}

export default commentReducer;
