import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Redirect, useHistory, useParams } from "react-router-dom";
import { editCurrentSong, deleteCurrentSong } from "../store/songFile"

import { getComments, addComment, deleteComment } from "../store/comment"


function EditSong() {
    const sessionUser = useSelector((state) => state.session.user);
    const dispatch = useDispatch();
    const songId = useParams().id;

    const song = useSelector((state) => state.songFile[songId]);

    const [songTitle, setSongTitle] = useState(`${song.title}`);

    const comments = useSelector((state) => state.comments);
    const [comment, setComment] = useState("");

    const history = useHistory();

    if (!sessionUser) return <Redirect to='/' />;

    // useEffect(() => {
    //     dispatch(getComments(song))
    // })

    const commentSubmit = async (e) => {
        e.preventDefault();
        const payload = {
            sessionUser,
            comment,
            song
        }
        console.log(payload)
    }

    const handleSubmit = async (e) => {
        e.preventDefault();

        const data = {
            id: song?.id,
            songTitle
        };
        console.log(data)
        await dispatch(editCurrentSong(data));
        history.push(`/songs`)
    }

    const handleDelete = (song) => {
        dispatch(deleteCurrentSong(song))
    }


    return (
        <div>
            <p>{song?.title}</p>
            <div>
                {Object.values(comments).map}
            </div>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={songTitle}
                    onChange={(e) => setSongTitle(e.target.value)}
                />
                <button type="submit">Submit</button>
            </form>
            <button
            onClick={(e) => handleDelete(song)}
            >Delete
            </button>
        </div>
        )
}


export default EditSong;
