import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Redirect, useHistory, useParams } from "react-router-dom";
import { editCurrentSong, deleteCurrentSong } from "../store/songFile"

import { getAllComments, createComment, removeComment } from "../store/comment"


function EditSong() {

    const dispatch = useDispatch();
    const songId = useParams().id;
    const sessionUser = useSelector((state) => state.session.user);

    const song = useSelector((state) => state.songFile[songId]);

    const { id } = useParams();

    const [title, setTitle] = useState(`${song.title}`);

    const comments = useSelector((state) => state.Comments);
    console.log(state.songFile)
    // const [body, setComment]
    const history = useHistory();


    if (!sessionUser) return <Redirect to='/' />;

    // const handleComment = async (e) => {
    //     e.preventDefault();
    //     const data = {
    //         sessionUser,

    //     }
    // }

    const handleSubmit = async (e) => {
        e.preventDefault();

        const data = {
            id: song?.id,
            title
        };

        await dispatch(editCurrentSong(data));
        history.push(`/songs`)
    }

    const handleDelete = (song) => {
        dispatch(deleteCurrentSong(song))
    }


    return (
        <>
            <div>
                <p>{song?.title}</p>
                <div>
                    {Object.values(song.Comments).map((singlecomment) => {
                        return (
                            <div key={singlecomment.id}>{singlecomment.body}</div>
                        )
                    })}
                </div>


                <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                    />
                    <button type="submit">Submit</button>
                </form>
                <button
                    onClick={(e) => handleDelete(song)}
                >Delete
                </button>
            </div>
        </>
    )
}


export default EditSong;
